    private void Btn_UpdateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Btn_UpdateActionPerformed

    String ID=Txt_Count.getText();
       String Cat= Crs_CB.getSelectedItem().toString();
       String Question=Txt_Q.getText();
       String Opt_1=Txt_O1.getText();
       String Opt_2=Txt_O2.getText();
       String Opt_3=Txt_O3.getText();
       String Opt_4=Txt_O4.getText();
       String Answer=Txt_A.getText();
       try
        {
            Connection Con=Connect.getcon();
            PreparedStatement ps=Con.prepareStatement("update tbl_q set Question=?, Opt_1=?,Opt_2=?, Opt_3=?,Opt_4=? ,Answer=? where ID=? and ,Cat=?");
            ps.setString(1, Question);
            
            ps.setString(2, Opt_1);
            ps.setString(3, Opt_2);
            ps.setString(4, Opt_3);
            ps.setString(5, Opt_4);
            ps.setString(6, Answer);
            ps.setString(7, ID);
            ps.setString(8, Cat);
            ps.executeUpdate();
            JFrame j=new JFrame();
             j.setAlwaysOnTop(true);
             JOptionPane.showMessageDialog(j, "Successfully Updated");
             setVisible(false);
             new InsertQuestion().setVisible(true);
             
        }
        catch(Exception e)
        {
            System.out.println("Exception"+e.getMessage());
        }


    }//GEN-LAST:event_Btn_UpdateActionPerformed

