    private void Btn_SearchActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Btn_SearchActionPerformed
        String ID=Txt_Count.getText();
        //String Cat= Crs_CB.getSelectedItem().toString();
        try
        {
            Connection Con=Connect.getcon();
            Statement st=Con.createStatement();
            ResultSet rs=st.executeQuery("select * from tbl_q where ID='"+ID+"'");
            if(rs.first())
            {
                Txt_Q.setText(rs.getString(3));
                Txt_O1.setText(rs.getString(4));
                Txt_O2.setText(rs.getString(5));
                Txt_O3.setText(rs.getString(6));
                Txt_O4.setText(rs.getString(7));
                Txt_A.setText(rs.getString(8));
                Txt_Count.setEditable(true);
            }
            else
            {
                JFrame j=new JFrame();
                j.setAlwaysOnTop(true);
                JOptionPane.showMessageDialog(j, "Question Not Found");
            }
        }
        catch(Exception e)
        {
            System.out.println("Exception"+e.getMessage());
        }
    }//GEN-LAST:event_Btn_SearchActionPerformed

